// shared/schema.ts
import { pgTable, text, serial, integer, decimal, timestamp } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
var users = pgTable("users", {
  id: serial("id").primaryKey(),
  username: text("username").notNull().unique(),
  password: text("password").notNull()
});
var renovationProjects = pgTable("renovation_projects", {
  id: serial("id").primaryKey(),
  userId: integer("user_id").references(() => users.id),
  renovationType: text("renovation_type").notNull(),
  postcode: text("postcode").notNull(),
  budgetMin: decimal("budget_min", { precision: 10, scale: 2 }),
  budgetMax: decimal("budget_max", { precision: 10, scale: 2 }),
  style: text("style").notNull(),
  timeline: text("timeline").notNull(),
  urgency: text("urgency"),
  additionalNotes: text("additional_notes"),
  createdAt: timestamp("created_at").defaultNow()
});
var insertUserSchema = createInsertSchema(users).pick({
  username: true,
  password: true
});
var insertRenovationProjectSchema = createInsertSchema(renovationProjects).omit({
  id: true,
  createdAt: true
});

// server/routes.ts
import { z } from "zod";
async function registerRoutes(app2) {
  app2.post("/api/renovation-projects", async (req, res) => {
    try {
      const projectData = insertRenovationProjectSchema.parse(req.body);
      const project = await storage.createRenovationProject(projectData);
      res.json(project);
    } catch (error) {
      if (error instanceof z.ZodError) {
        res.status(400).json({ error: "Invalid project data", details: error.errors });
      } else {
        res.status(500).json({ error: "Failed to create project" });
      }
    }
  });
  app2.get("/api/renovation-projects/user/:userId", async (req, res) => {
    try {
      const userId = parseInt(req.params.userId);
      if (isNaN(userId)) {
        return res.status(400).json({ error: "Invalid user ID" });
      }
      const projects = await storage.getRenovationProjectsByUser(userId);
      res.json(projects);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch projects" });
    }
  });
  const httpServer = createServer(app2);
  return httpServer;
}

